package com.gsj.study.leetcode.easy;

import java.util.Arrays;

public class LongestSubstring {
    public static void main(String[] args) {
        LongestSubstring longestSubstring = new LongestSubstring();
//        String s = "";
//        String s = "aaabb";
        String s = "ababacb";
        System.out.println(longestSubstring.longestSubstring(s, 3));

//        System.out.println((int) 'a' + ", " + (int) 'z');

    }

    public int longestSubstring(String s, int k) {
        int length = s.length();
        int windSize = 0;
        int max = 0;
        int[] nums = new int[26];


        Arrays.fill(nums, 0);
        int i = 0;
        for (i = 0; i < length; i++) {
            char c = s.charAt(i);
            nums[(int) c - 97]++;
        }

        for (int j = 0; j < nums.length; j++) {
            if (nums[j] >= k) {
                windSize += nums[j];
            }
        }
        if(windSize == length){
            return windSize;
        }

        for (; windSize >= k; windSize--) {
            boolean find = false;
            for (int start = 0; start <= length - windSize; start++) {
                find = true;
                Arrays.fill(nums, 0);
                for (i = start; i < start + windSize; i++) {
                    char c = s.charAt(i);
                    nums[(int) c - 97]++;
                }
                int j = 0;
                for (; j < nums.length; j++) {
                    if (nums[j] < k && nums[j] > 0) {
                        find = false;
                        break;
                    }
                }

                if (find) {
                    max = windSize;
                    break;
                }
            }
            if (find) {
                max = windSize;
                break;
            }
        }


        return max;
    }
}
